package main

import (
	"encoding/base64"
	"fmt"
	"github.com/forgoer/openssl"
)

func main() {
	var content = ""

	//tool := encry.NewAesTool([]byte("aaaabbbbccccdddd"), 16)
	decodeBytes, _ := base64.StdEncoding.DecodeString(content)

	dst, _ := openssl.AesECBDecrypt(decodeBytes, []byte("aaaabbbbccccdddd"), openssl.PKCS7_PADDING)
	fmt.Println(string(dst))

}
